mapper.delete:
delete from auto_car



            car_id = #{carId}


            car_id in

                #{carId}



            car_name = #{carName}


            brand_id = #{brandId}


            brand_cat_id = #{brandCatId}


            brand_model_id = #{brandModelId}


            province = #{province}


            city = #{city}


            area = #{area}


            add_user_id = #{addUserId}


            car_type = #{carType}


            status = #{status}


            sort_order = #{sortOrder}

mapper.select:
select ac.*, ab.brand_name,abc.cat_name,abm.model_name,au.user_name,aup.real_name,au.status as add_user_status,
(select count(1) from auto_collect as auc where auc.target_type = 1 and auc.target_id = ac.car_id) as collect_count

    ,(select collect_id from auto_collect as auc where auc.target_type = 1 and auc.target_id = ac.car_id and auc.user_id = #{viewUserId}) as collect_id

from auto_car as ac
inner join auto_user as au on au.user_id = ac.add_user_id
left join auto_user_profile as aup on aup.user_id=ac.add_user_id
inner join auto_brand as ab on ac.brand_id=ab.brand_id
inner join auto_brand_cat as abc on ac.brand_cat_id=abc.cat_id
inner join auto_brand_model as abm on ac.brand_model_id=abm.model_id



            and ac.car_id in

                ${carId}



            and ac.car_name like concat('%',#{carName},'%')


            and ac.car_type=#{carType}


            and ac.brand_id = #{brandId}


            and ac.brand_id in (${brandIds})


            and ac.shop_price >= #{startPrice}


            and ac.shop_price <= #{endPrice}


            and ac.start_date >= #{startDate}


            and ac.end_date <= #{endDate}


            and ac.car_type = #{carType}


            and ac.status = #{status}


            and ac.add_user_id = ${addUserId}


            ${filter}


            and #{curTime} >= ac.start_date and #{curTime} <= ac.end_date



order by

    ${sort},

ac.refresh_time desc, ac.car_id desc

select ac.*, ab.brand_name,abc.cat_name,abm.model_name,au.user_name, au.status as userStatus,act.collect_id as collectId from auto_car as ac inner join auto_user as au on au.user_id = ac.add_user_id inner join auto_brand as ab on ab.brand_id = ac.brand_id inner join auto_brand_cat as abc on abc.cat_id = ac.brand_cat_id inner join auto_brand_model as abm on abm.model_id = ac.brand_model_id inner join auto_collect as act on act.target_id=ac.car_id and act.target_type = 1
and act.user_id=${userId}
order by act.collect_id desc

select ac.*, ab.brand_name,abc.cat_name,abm.model_name,
au.status as add_user_status,ao.add_time as add_order_time
from auto_car as ac
inner join auto_user as au on au.user_id = ac.add_user_id
inner join auto_brand as ab on ac.brand_id=ab.brand_id
inner join auto_brand_cat as abc on ac.brand_cat_id=abc.cat_id
inner join auto_brand_model as abm on ac.brand_model_id=abm.model_id
left join auto_order_car as aoc on aoc.car_id = ac.car_id
left join auto_order as ao on ao.order_id = aoc.order_id



            and ac.add_user_id = ${addUserId}


            and ao.seller_id = ${sellerId}


            and ao.buyer_id = ${buyerId}



            and ao.order_status in (${orderStatusList})


            and ao.pay_status in (${payStatusList})


            and ao.ship_status = ${shipStatus}


            and ao.pay_status = ${payStatus}


            and ao.order_status = ${orderStatus}


            and #{curTime} >= start_date and #{curTime} <= end_date


            and ${filter}



order by ${sort} ac.refresh_time desc, ac.car_id desc
