mapper.delete:
delete from auto_car



            car_id = #{carId}


            car_id in

                #{carId}



            car_name = #{carName}


            brand_id = #{brandId}


            brand_cat_id = #{brandCatId}


            brand_model_id = #{brandModelId}


            total_number = #{totalNumber}


            lock_number = #{lockNumber}


            province = #{province}


            city = #{city}


            area = #{area}


            add_user_id = #{addUserId}


            car_type = #{carType}


            status = #{status}


            sort_order = #{sortOrder}

mapper.select:
select ac.*, ab.brand_name,abc.cat_name,abm.model_name,au.user_name,aup.real_name,au.status as user_status
from auto_car as ac
inner join auto_user as au on au.user_id = ac.add_user_id
left join auto_user_profile as aup on aup.user_id=ac.add_user_id
inner join auto_brand as ab on ac.brand_id=ab.brand_id
inner join auto_brand_cat as abc on ac.brand_cat_id=abc.cat_id
inner join auto_brand_model as abm on ac.brand_model_id=abm.model_id


        and ac.car_id in

            ${carId}



        and ac.car_name like concat('%',#{carName},'%')


        and ac.car_type=#{carType}


        and ac.brand_id = #{brandId}


        and ac.brand_id in (${brandIds})


        and ac.shop_price >= #{startPrice}


        and ac.shop_price <= #{endPrice}


        and ac.start_date >= #{startDate}


        and ac.end_date <= #{endDate}


        and ac.car_type = #{carType}


        and ac.status = #{status}


        and ac.add_user_id = ${addUserId}


order by ${sort} ac.add_time desc, ac.car_id desc

select ac.*, ab.brand_name,abc.cat_name,abm.model_name,au.user_name, au.status as userStatus,act.collect_id as collectId from auto_car as ac inner join auto_user as au on au.user_id = ac.add_user_id inner join auto_brand as ab on ac.brand_id=ab.brand_id inner join auto_brand_cat as abc on ac.brand_cat_id=abc.cat_id inner join auto_brand_model as abm on ac.brand_model_id=abm.model_id inner join auto_collect as act on ac.car_id=act.target_id
and act.user_id=${userId}
and ${collectSql}
order by act.add_time desc,act.collect_id desc LIMIT 0,10

select ac.*, ab.brand_name,abc.cat_name,abm.model_name,
au.status as userStatus,ao.add_time as addOrderTime
from auto_car as ac
inner join auto_user as au on au.user_id = ac.add_user_id
inner join auto_brand as ab on ac.brand_id=ab.brand_id
inner join auto_brand_cat as abc on ac.brand_cat_id=abc.cat_id
inner join auto_brand_model as abm on ac.brand_model_id=abm.model_id
left join auto_order_car as aoc on aoc.car_id = ac.car_id
left join auto_order as ao on ao.order_id = aoc.order_id


        and ac.add_user_id = ${userId}


        and ao.seller_id = ${sellerId}


        and ${filter}


        and ao.order_status in (${orderStatusList})


        and ao.pay_status in (${payStatusList})


        and ao.ship_status = ${shipStatus}


        and ao.order_status = ${orderStatus}


        and ${carSql}


order by ${sort} ac.refresh_time desc, ac.car_id desc LIMIT 0,10
