mapper.delete:
        delete from auto_order



	                order_id = #{orderId}


                    order_id in

                        #{orderId}



	                order_sn = #{orderSn}

mapper.select:
select ao.*,aoc.car_number as carNumber,aoc.has_parts as hasParts,aoc.car_attr_id as carAttrId, (select au.user_name from auto_user as au where ao.seller_id=au.user_id) as sellerName, (select aup.real_name from auto_user_profile as aup where ao.seller_id=aup.user_id) as sellerRealName, (select au.user_name from auto_user as au where ao.buyer_id=au.user_id) as buyerName, (select aup.real_name from auto_user_profile as aup where ao.buyer_id=aup.user_id) as buyerRealName from auto_order as ao inner join auto_order_car as aoc on ao.order_id=aoc.order_id
ao.order_id = #{orderId}
and ao.order_sn = #{orderSn}

select  from auto_order as ao



            and order_id = #{orderId}


            and order_sn = #{orderSn}


            and order_status = #{orderStatus}


            and pay_status = #{payStatus}


            and ship_status = #{shipStatus}


            and order_id in

                ${orderId}



            and order_status in (${orderStatusList})


            and pay_status in (${payStatusList})


            and ship_status in (${shipStatusList})


            and seller_id = ${sellerId}


            and buyer_id = ${buyerId}

select ao.*,ac.car_name as carName,aoc.car_number as carNumber,
(select au.user_name from auto_user as au where ao.seller_id=au.user_id) as sellerName,
(select aup.real_name from auto_user_profile as aup where ao.seller_id=aup.user_id) as sellerRealName,
(select au.user_name from auto_user as au where ao.buyer_id=au.user_id) as buyerName,
(select aup.real_name from auto_user_profile as aup where ao.buyer_id=aup.user_id) as buyerRealName
from auto_order as ao
inner join auto_order_car as aoc on ao.order_id=aoc.order_id
inner join auto_car as ac on aoc.car_id=ac.car_id
inner join auto_car_attr as aca on ac.car_id=aca.car_id and aoc.car_attr_id=aca.car_attr_id


        and ao.order_status = ${orderStatus}


        and ao.pay_status = ${payStatus}


        and ao.ship_status = ${shipStatus}


        and ao.add_time >= #{beginTime}


        and ao.add_time <= #{endTime}


order by ao.add_time desc,ao.order_id desc

select count(1) from auto_order as ao



            and ao.add_time >= ${startTime}


            and ao.add_time <= ${endTime}
